import "./ChartBar.css";

function ChartBar(props) {
  let barFillHeight = "0%";
  if (props.Max > 0) {
    barFillHeight = Math.round((props.value / props.maxValue) * 100) + "%";
  }

  return (
    <div className="chart-bar">
      <div classname="inner">
        <div
          className="fill"
          style={{
            height: barFillHeight,
          }}
        ></div>
      </div>
      <div className="label">{props.label}</div>
    </div>
  );
}

export default ChartBar;
