import { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = new useState("2020");
  const [filteredItembyYear, setFilteredItembyYear] = new useState(props.items); //ADDED new usestate with expenses as default to be filtered later on
  function filterChangeHandler(selectedYear) {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
    //BEGIN to set the new usestate with the new value whichj filtered by year
    setFilteredItembyYear(
      props.items.filter(function (expenses) {
        console.log(filteredItembyYear);
        return expenses.date.getFullYear() == selectedYear;
      })
    );
    //END to set the new usestate with the new value whichj filtered by year
  }
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        ></ExpensesFilter>
        {filteredItembyYear.map(function (
          expense //EDITED now use the new usestate to map the item
        ) {
          return (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            ></ExpenseItem>
          );
        })}
      </Card>
    </div>
  );
}

export default Expenses;
